import type * as T from "./types"

export const addCard = (payload: T.Card): T.AddCardAction => ({
  type: "@cardEntities/add",
  payload
})

export const removeCard = (payload: T.UUID): T.RemoveCardAction => ({
  type: "@cardEntities/remove",
  payload
})
