import type {ChangeEvent} from "react"
import {useState, useCallback, useEffect} from "react"
import {Link, useNavigate} from "react-router-dom"
import {useAuth} from "../../contexts"
import * as U from "../../utils"

type LoginFromType = Record<"email" | "password", string>
const initialFormState = {email: "", password: ""}

export default function Login() {
  const [{email, password}, setForm] = useState<LoginFromType>(initialFormState)
  const changed = useCallback(
    (key: string) => (e: ChangeEvent<HTMLInputElement>) => {
      setForm(obj => ({...obj, [key]: e.target.value}))
    },
    []
  )

  const navigate = useNavigate()
  const {login} = useAuth()
  const loginAccount = useCallback(() => {
    login(email, password, () => navigate("/"))
  }, [email, password, navigate, login])

  useEffect(() => {
    U.readObjectP<LoginFromType>("user")
      .then(user => {
        if (user) setForm(user)
      })
      .catch(e => {})
  }, [])

  return (
    <div className="flex flex-col min-h-screen border-gray-300 rounded-xl shadow-xl bg-gray-100 border">
      <div className="flex flex-col items-center justify-center flex-1 max-w-sm px-2 mx-auto">
        <div className="w-full px-6 py-8 text-black bg-white rounded shadow-md">
          <h1 className="mb-8 text-2xl text-center text-primary">Sign Up</h1>
          <input
            type="text"
            className="w-full p-3 mb-4 input input-primary"
            name="email"
            placeholder="Email"
            value={email}
            onChange={changed("email")}
          />
          <input
            type="password"
            className="w-full p-3 mb-4 input input-primary"
            name="password"
            placeholder="password"
            value={password}
            onChange={changed("password")}
          />
          <input
            type="password"
            className="w-full p-3 mb-4 input input-primary"
            name="confirm_password"
            placeholder="Confirm Password"
            value={confirmPassword}
            onChange={changed("confirmPassword")}
          />
          <button
            type="submit"
            className="w-full btn btn-primary"
            onClick={createAccount}>
            CREATE ACCOUNT
          </button>
        </div>

        <div className="mt-6 text-green-800">
          Already have an account?
          <Link className="btn btn-link btn-primary" to="/login/">
            LOG IN
          </Link>
        </div>
      </div>
    </div>
  )
}
